Given the following HTML:

<div class="bg-background text-primary-foreground min-h-screen flex flex-col md:flex-row items-center">
    <div class="w-full md:w-1/2 flex flex-col items-center justify-center">
        <h1 class="text-3xl font-bold mb-4">AI Upload System</h1>
        <div class="bg-card w-full p-6 rounded-lg shadow-lg">
            <label for="fileInput" class="block text-sm font-medium leading-5">Select File:</label>
            <input id="fileInput" type="file" class="w-full mt-1 py-2 px-3 block border border-input rounded-md bg-input text-primary-foreground" accept=".jpg, .jpeg, .png" />
            <button id="uploadBtn" class="w-full mt-4 bg-primary text-primary-foreground py-2 px-4 rounded-md hover:bg-primary/80 transition duration-300">Upload & Process</button>
        </div>
        <div id="preview" class="mt-8 w-full"></div>
    </div>
    <div id="history" class="mt-8 w-full md:w-1/2 bg-card p-4 rounded-lg shadow-lg">
        <h2 class="text-lg font-bold mb-2">Upload History</h2>
        <ul id="historyList" class="divide-y divide-zinc-200"></ul>
    </div>
</div>

<script>
    const fileInput = document.getElementById('fileInput');
    const uploadBtn = document.getElementById('uploadBtn');
    const preview = document.getElementById('preview');
    const historyList = document.getElementById('historyList');

    fileInput.addEventListener('change', function() {
        const file = fileInput.files[0];
        const reader = new FileReader();
        reader.onload = function(e) {
            const img = document.createElement('img');
            img.src = e.target.result;
            img.alt = "Uploaded Image";
            img.className = "w-full rounded-lg";
            preview.innerHTML = '';
            preview.appendChild(img);
        }
        reader.readAsDataURL(file);
    });

    uploadBtn.addEventListener('click', function() {
        // Add AI processing logic here
        alert("AI processing in progress...");

        // Add uploaded image to history
        if (preview.firstChild) {
            const imgClone = preview.firstChild.cloneNode(true);
            const li = document.createElement('li');
            li.className = "flex items-center py-2";
            li.appendChild(imgClone);
            historyList.prepend(li);
        }
    });
</script>

