// search.component.ts

import { Component } from '@angular/core';

const inputClasses = 'w-full bg-transparent focus:outline-none text-primary-foreground';
const buttonClasses = 'text-primary ml-2';
const selectClasses = 'block appearance-none w-full bg-input border border-primary text-primary-foreground py-3 px-4 pr-8 rounded-lg leading-tight focus:outline-none focus:bg-input focus:border-primary';
const arrowIconClasses = 'fill-current h-4 w-4';

@Component({
  selector: 'app-search',
  template: `
    <div class="bg-background text-primary-foreground min-h-screen flex flex-col items-center justify-center">
      <h1 class="text-3xl font-bold mb-8">Search Engine</h1>
      <div class="w-full max-w-md flex items-center bg-input rounded-lg p-2">
        <input type="text" placeholder="Search..." class="{{ inputClasses }}" />
        <button class="{{ buttonClasses }}">
          <img aria-hidden="true" alt="search-icon" src="https://placehold.co/24.png?text=üîç" />
        </button>
      </div>
      <div class="relative mt-4">
        <select class="{{ selectClasses }}">
          <option>Select District</option>
          <option>Blantyre</option>
          <!-- Add more options here -->
        </select>
        <div class="pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-primary-foreground">
          <svg class="{{ arrowIconClasses }}" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20">
            <path d="M10 12l-5-5 1.5-1.5L10 9l3.5-3.5L15 7z"/>
          </svg>
        </div>
      </div>
    </div>
  `
})
export class SearchComponent {
  inputClasses = inputClasses;
  buttonClasses = buttonClasses;
  selectClasses = selectClasses;
  arrowIconClasses = arrowIconClasses;
}
```

