  const addOrderBtn = document.getElementById('addOrder');
  const orderTable = document.getElementById('orderTable');

  addOrderBtn.addEventListener('click', () => {
    const bookName = document.getElementById('bookName').value;
    const quantity = document.getElementById('quantity').value;
    const price = document.getElementById('price').value;
    const total = (parseFloat(quantity) * parseFloat(price)).toFixed(2);

    const row = orderTable.insertRow();
    row.innerHTML = `
      <td class="border border-input px-4 py-2">${bookName}</td>
      <td class="border border-input px-4 py-2">${quantity}</td>
      <td class="border border-input px-4 py-2">${price}</td>
