        <button class="bg-destructive text-destructive-foreground px-2 py-1 rounded-md ml-2 hover:bg-destructive/80 transition-colors">Âà†Èô§‰ø°ÊÅØ</button>
      </td>
    `;
  });

  orderTable.addEventListener('click', (e) => {
    if (e.target.classList.contains('bg-accent')) {
      const row = e.target.parentNode.parentNode;
      row.classList.add('bg-primary', 'text-primary-foreground');
      const cells = row.querySelectorAll('td');
      cells[1].innerHTML = `<input type="number" value="${cells[1].textContent}" class="w-16 p-1 border border-input rounded-md" />`;
      e.target.innerHTML = 'Á°ÆÂÆö‰øÆÊîπ';
      e.target.classList.remove('bg-accent', 'hover:bg-accent/80');
      e.target.classList.add('bg-primary', 'hover:bg-primary/80');
    } else if (e.target.classList.contains('bg-primary')) {
      const row = e.target.parentNode.parentNode;
      const cells = row.querySelectorAll('td');
      const quantityInput = cells[1].querySelector('input');
      const price = cells[2].textContent;
      const newQuantity = quantityInput.value;
      const newTotal = (parseFloat(newQuantity) * parseFloat(price)).toFixed(2);
      cells[1].textContent = newQuantity;
      cells[3].textContent = newTotal;
      row.classList.remove('bg-primary', 'text-primary-foreground');
      quantityInput.remove();
      e.target.innerHTML = '‰øÆÊîπÊï∞Èáè';
      e.target.classList.remove('bg-primary', 'hover:bg-primary/80');
      e.target.classList.add('bg-accent', 'hover:bg-accent/80');
    } else if (e.target.classList.contains('bg-destructive')) {
      const row = e.target.parentNode.parentNode;
      row.remove();
    }
  });
</script>
