<div class="max-w-lg mx-auto mt-8 bg-card dark:bg-card-foreground rounded-lg shadow-lg">
  <div class="p-6">
    <label for="search" class="block text-lg font-semibold text-primary-foreground dark:text-primary mb-2">Search</label>
    <input id="search" type="text" placeholder="Type to search..." class="w-full px-4 py-2 rounded-lg bg-input dark:bg-primary-foreground text-primary dark:text-primary-foreground border border-primary dark:border-primary-foreground focus:outline-none focus:ring focus:ring-primary dark:focus:ring-primary-foreground">
    <div id="autocomplete" class="mt-2"></div>
  </div>
</div>

<script>
  const searchInput = document.getElementById('search');
  const autocomplete = document.getElementById('autocomplete');

  const suggestions = ['Apple', 'Banana', 'Cherry', 'Date', 'Elderberry', 'Fig', 'Grape', 'Kiwi', 'Lemon', 'Mango'];

  searchInput.addEventListener('input', () => {
    const query = searchInput.value.toLowerCase();
    const filteredSuggestions = suggestions.filter(suggestion => suggestion.toLowerCase().includes(query));

    autocomplete.innerHTML = '';
    filteredSuggestions.forEach(suggestion => {
      const suggestionElement = document.createElement('div');
      suggestionElement.textContent = suggestion;
      suggestionElement.classList.add('p-2 cursor-pointer hover:bg-primary/20', 'dark:hover:bg-primary-foreground/20');
      suggestionElement.addEventListener('click', () => {
        searchInput.value = suggestion;
        autocomplete.innerHTML = '';
      });
      autocomplete.appendChild(suggestionElement);
    });
  });
</script>
