<div class="max-w-md mx-auto mt-10 p-6 bg-card text-card-foreground rounded-lg shadow-lg">
  <form id="date-range-form" class="space-y-4">
    <div>
      <label for="start-date" class="block text-sm font-medium text-muted-foreground">Start Date</label>
      <input type="date" id="start-date" name="start-date" class="mt-1 block w-full border border-input rounded-md shadow-sm focus:ring-primary focus:border-primary sm:text-sm" required>
    </div>
    <div>
      <label for="end-date" class="block text-sm font-medium text-muted-foreground">End Date</label>
      <input type="date" id="end-date" name="end-date" class="mt-1 block w-full border border-input rounded-md shadow-sm focus:ring-primary focus:border-primary sm:text-sm" required>
    </div>
    <button type="submit" class="w-full bg-primary text-primary-foreground py-2 px-4 rounded-md hover:bg-primary/80 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary">
      Submit
    </button>
  </form>
</div>
<script>
  document.getElementById('date-range-form').addEventListener('submit', function(event) {
    event.preventDefault();
    const startDate = document.getElementById('start-date').value;
    const endDate = document.getElementById('end-date').value;
    if (new Date(startDate) > new Date(endDate)) {
      alert('End date must be after start date.');
    } else {
      alert(`Date range selected: ${startDate} to ${endDate}`);
    }
  });
</script>
